import { Component } from '@angular/core';

@Component({
  selector: 'app-main',
  templateUrl: './main.component.html',
  styleUrls: ['./main.component.css']
})
export class MainComponent {

  constructor(private http: HttpClient, private sanitizer: DomSanitizer) {}

  descargarPDF() {
    const pdfUrl = '/assets/tu-archivo.pdf'; // Ruta relativa al PDF en la carpeta assets
    const pdfNombre = 'mi_cv.pdf'; // Nombre del archivo PDF que se descargará

    this.http.get(pdfUrl, { responseType: 'arraybuffer' }).subscribe((data: ArrayBuffer) => {
      const blob = new Blob([data], { type: 'application/pdf' });

      const url = window.URL.createObjectURL(blob);
      const enlace = document.createElement('a');

      enlace.href = url;
      enlace.download = pdfNombre;

      // Simula un clic en el enlace para iniciar la descarga
      enlace.click();

      window.URL.revokeObjectURL(url);
    });
  }
}
